/bin/bash -c 'sudo apt-get update && sudo apt install -y nfs-common jq && echo "Starting startup script" && storage_account=$( echo "$storage_account_container_map" | jq keys -c) && for store in $(echo $storage_account | jq -r -c '.[]');do for container_name in $( echo "$storage_account_container_map" | jq .$store -c | jq -r -c '.[]' );do sudo echo "${store}${env}.blob.core.windows.net:/${store}${env}/$container_name  /$container_name    nfs defaults,sec=sys,vers=3,nolock,proto=tcp,nofail    0 0" >> /etc/fstab; sudo mkdir -p /$container_name; sudo mount /$container_name; done done && sudo wget  -O - https://raw.githubusercontent.com/Azure/batch-insights/master/scripts/run-linux.sh | bash'