/bin/bash -c 'sudo apt-get update && sudo apt install -y nfs-common jq && echo "Starting startup script" && storage_account=$( echo "$storage_account_container_map" | jq keys -c) && for store in $(echo $storage_account | jq -r -c '.[]');do for container_name in $( echo "$storage_account_container_map" | jq .$store -c | jq -r -c '.[]' );do sudo echo "${store}${suffix}${env}.blob.core.windows.net:/${store}${suffix}${env}/$container_name  /$container_name    nfs defaults,sec=sys,vers=3,nolock,proto=tcp,nofail    0 0" >> /etc/fstab; sudo mkdir -p /$container_name; sudo mount /$container_name; done done && sudo wget  -O - https://raw.githubusercontent.com/Azure/batch-insights/master/scripts/run-linux.sh | bash && sudo  apt-get -y install python3-pip && pip install azure-batch==12.0.0 azure-keyvault==4.2.0 azure-identity==1.11.0 pydantic==1.10.2 python-dotenv==0.21.0 requests==2.28.1 azure-storage-blob && wget https://aka.ms/downloadazcopy-v10-linux && tar -xvf downloadazcopy-v10-linux && sudo cp ./azcopy_linux_amd64_*/azcopy /usr/bin/'